$(document).ready(function(){$("#form").submit(function(c){c.preventDefault();var v=$("#trend").val().toLowerCase(),t=$("#fundamental").val(),r=$("#comparison").val(),m=$("#percentage").val(),l=$("#startDate").val(),o=$("#endDate").val();if(alert(l+" "+o),t=="Debt to Equity Ratio")var i="der";else if(t=="Loan to Deposit Ratio")var i="loan_to_depo_ratio";if(r=="Less than")var s="<";else if(r=="More than")var s=">";$.ajax({type:"GET",url:"/search/technical",headers:{Accept:"application/json"},data:{_token:$("input[name='_token']").val(),param:i,comparison:s,num:m,trend:v,start:l,end:o},success:function(e){if(console.log(e),!e)alert("Data tidak ditemukan");else for(var d=document.getElementById("tableData").getElementsByTagName("tbody")[0],n=0;n<e.length;n++){var a=d.insertRow(),u=a.insertCell(),p=a.insertCell(),f=a.insertCell(),g=a.insertCell(),L=a.insertCell(),h=a.insertCell();let T=parseInt(e[n].change).toFixed(2);u.innerHTML=n+1,p.innerHTML=e[n].ticker,f.innerHTML=e[n].trend,g.innerHTML=T+"%",L.innerHTML=e[n].der+"%",h.innerHTML=e[n].ldr+"%"}},error:function(e){console.log(e)}})})});
